import AnchorLink from "react-anchor-link-smooth-scroll"

const DotGroup = ({ selectedPage, setSelectedPage}) => {
    const selectedStyles = `relative bg-yellow-500 before:absolute before:w-6 before:h-6
        before:rounded-full before:border-2 before:border-yellow-500 before:left-[-50%]
        before:top-[-50%]`;

    return <div className="flex flex-col gap-6 fixed top-[60%] right-7">
        <AnchorLink
            className={`${selectedPage === 'home' ? selectedStyles : "bg-gray-500"}
        w-3 h-3 rounded-full`}
        href="#home"
        onClick={() => setSelectedPage('home')}
        />
        <AnchorLink
            className={`${selectedPage === 'A propos de moi' ? selectedStyles : "bg-gray-500"}
        w-3 h-3 rounded-full`}
        href="#A propos de moi"
        onClick={() => setSelectedPage('a propos de moi')}
        />
        <AnchorLink
            className={`${selectedPage === 'skills' ? selectedStyles : "bg-gray-500"}
        w-3 h-3 rounded-full`}
        href="#skills"
        onClick={() => setSelectedPage('skills')}
        />
        <AnchorLink
            className={`${selectedPage === 'projets' ? selectedStyles : "bg-gray-500"}
        w-3 h-3 rounded-full`}
        href="#projets"
        onClick={() => setSelectedPage('projets')}
        />
        <AnchorLink
            className={`${selectedPage === 'contact' ? selectedStyles : "bg-gray-500"}
        w-3 h-3 rounded-full`}
        href="#contact"
        onClick={() => setSelectedPage('contact')}
        />
    </div>;
};

export default DotGroup;
