import LineGradient from "../components/LineGradient";
import useMediaQuery from "../hooks/useMediaQuery";
import { motion } from "framer-motion";

const About = () => {
    const isAboveMediumScreens = useMediaQuery("(min-width: 1060px)");

    return (
        <section id="about" className="pt-10 pb-25">
            {/*header et image section*/}
            <div className="md:flex md:justify-between md:gap-16 mt-32">
                <motion.div
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, amount: 0.5 }}
                    transition={{ duration: 0.5 }}
                    variants={{
                        hidden: { opacity: 0, x: -50 },
                        visible: { opacity: 1, x: 0 },

                    }}
                >
                    <p className="font-playfair font semibold text-4xl mb-5">
                        Ã€ <span className="text-red-400" >SKILLS</span>
                    </p>
                    <LineGradient width="w-1/4" />
                    <p className="mt-10 mb-7">
                        HTML / CSS /JS / ReactJS/
                    </p>
                </motion.div>

                <div className="mt-16 md:mt-0">
                    {isAboveMediumScreens ? (
                        <div
                            className="relative z-0 ml-20 before:absolute before:-top-10 before:-left-10 
                                before:w-full before:max-w-[800px] before:h-full before:border-2 before:border-blue-600 before:z-[-1]">
                            <img
                                alt="skills"
                                className="z-10"
                                src="assets/skills-image.png"
                            />
                        </div>
                    ) : (
                        <img alt="skills" className="z-10" src="assets/skills-image.png" />
                    )}
                </div>
            </div>
            {/*skills section*/}
            <div className="md:flex md:justify-between mt-16 gap-32">
                {/*Experince*/}
                <motion.div
                    className="md:w-1/3 mt-10"
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, amount: 0.5 }}
                    transition={{ duration: 0.5 }}
                    variants={{
                        hidden: { opacity: 0, y: 50 },
                        visible: { opacity: 1, y: 0 },
                    }}
                >

                </motion.div>



                {/*Objectifs*/}
                <motion.div
                    className="md:w-1/3 mt-10"
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, amount: 0.5 }}
                    transition={{ delay: 0.4, duration: 0.5 }}
                    variants={{
                        hidden: { opacity: 0, y: 50 },
                        visible: { opacity: 1, y: 0 },
                    }}
                >
                    <div className="relative h-32">
                        <div className="z-10">
                            <p className="font-playfair font-semibold text-5xl">03</p>
                            <p className="font-playfair font-semibold text-3xl mt-3">
                                Envie
                            </p>
                        </div>
                            <div className="w-1/2 md:w:-3/4 h-32 bg-yellow-400 absolute right-0 top-0 z-[-1]" />
                    </div>
                    <p className="mt-5">
                        Lorem, ipsum dolor sit amet consectetur adipisicing elit. 
                        Quidem dicta ad quasi ullam? 
                        Voluptatibus voluptatum impedit ipsum consectetur dolore omnis eligendi facere incidunt debitis, 
                        nostrum similique iste possimus cumque illo?
                    </p>
                </motion.div>
            </div>
        </section>
    )
};

export default About;
